import React, { Component } from 'react';
import './App.css';

class AllReviews extends Component {

    // Initialize state
    state = {
        reviews: []
    }

    componentDidMount() {
        let url = "http://localhost:3004/reviews";
        fetch(url)
            .then(resp => resp.json())
            .then(data => {
                let reviews = data.map((reviews, index) => {
                    let star = 0;
                    if (typeof reviews.star_rating != 'number') {
                        console.log(typeof reviews.star_rating);
                        star = parseInt(reviews.star_rating.toString);
                    }
                    else {
                        console.log("is a num");
                        star = reviews.star_rating;
                    }
                    return (
                        <div key={index}>
                            <h3>{reviews.headline}</h3>
                            <h3>{star} / 5 stars</h3>
                            <p>Written by: {reviews.author}</p>
                            <p>{reviews.body}</p>
                        </div>

                    )
                })
                this.setState({ reviews: reviews });
            })

    }



    render() {

        return (
            <div>
                <h1>Product Reviews</h1>
                {this.state.reviews}
            </div>
        );
    }
}

export default AllReviews;
