import React, { Component } from 'react';
import './App.css';

class AddReview extends Component {

    // Initialize state
    state = {
        author: "",
        stars: "",
        title: "",
        body: "",
    }

    componentDidMount() {

    }

    handleSubmit = async e => {
        console.log("Inside Blog.handleSubmit")
        e.preventDefault();

        var submitUrl = "http://localhost:3004/reviews";

        var data = {
            author: this.state.author,
            stars: this.state.stars,
            title: this.state.title,
            body: this.state.body
        };

        const response = await fetch(submitUrl, {
            method: 'POST',

            // need these headers for post
            headers: {
                'Content-Type': 'application/json',
            },

            body: JSON.stringify(data),
        });

        console.log("HEY " + this.state.userV);

    };


    render() {

        return (
            <div>
                <div>
                    <label>
                        Title:
                        <input
                            name="title"
                            type="text"
                            value={this.state.title} onChange={this.handleChangeTitle}
                        />
                    </label>
                </div>
                <div>
                    <label>
                        Body :
                        <textarea
                            name="paragraph_text"
                            cols="50"
                            rows="10"
                            value={this.state.body} onChange={this.handleChangeBody}
                        />
                    </label>
                </div>
                <div>
                    <label>
                        Stars :
                        <input
                            name="stars"
                            type="text"
                            value={this.state.stars} onChange={this.handleChangeStars}
                        />
                    </label>
                </div>
                <div>
                    <label>
                        Author :
                        <input
                            name="stars"
                            type="text"
                            value={this.state.author} onChange={this.handleChangeAuthor}
                        />
                    </label>
                </div>
                <div>
                    <button onClick={this.onSubmitForm}>Submit</button>
                </div>
            </div>
        );
    }
}

export default AddReview;
