import React, { Component } from 'react';
import './App.css';

class AddReview extends Component {

    // Initialize state
    state = {
        author: "",
        stars: 0,
        title: "",
        body: "",
    }

    componentDidMount() {

    }

    handleSubmit = async e => {
        console.log("Inside Blog.handleSubmit")
        e.preventDefault();

        var submitUrl = "http://localhost:3004/reviews";

        var data = {
            author: this.state.author,
            star_rating: this.state.stars,
            headline: this.state.title,
            body: this.state.body
        };

        const response = await fetch(submitUrl, {
            method: 'POST',

            // need these headers for post
            headers: {
                'Content-Type': 'application/json',
            },

            body: JSON.stringify(data),
        });

        console.log("HEY " + this.state.userV);

    };

    // handlers to set the state of each element
    handleChangeTitle = (e) => {
        this.setState({
            title: e.target.value
        });
    }

    handleChangeAuthor = (e) => {
        this.setState({
            author: e.target.value
        });
    }

    handleChangeBody = (e) => {
        this.setState({
            body: e.target.value
        });
    }

    handleChangeStars = (e) => {
        this.setState({
            stars: e.target.value
        });
    }


    render() {

        return (
            <div>
                <div>
                    <label>
                        Title:
                        <input
                            name="title"
                            type="text"
                            value={this.state.title} onChange={this.handleChangeTitle}
                        />
                    </label>
                </div>
                <div>
                    <label>
                        Body :
                        <textarea
                            name="paragraph_text"
                            cols="50"
                            rows="10"
                            value={this.state.body} onChange={this.handleChangeBody}
                        />
                    </label>
                </div>
                <div>
                    <label>
                        Stars :
                        <input
                            name="stars"
                            type="number"
                            value={this.state.stars} onChange={this.handleChangeStars}
                        />
                    </label>
                </div>
                <div>
                    <label>
                        Author :
                        <input
                            name="author"
                            type="text"
                            value={this.state.author} onChange={this.handleChangeAuthor}
                        />
                    </label>
                </div>
                <div>
                    <button onClick={this.handleSubmit}>Submit</button>
                </div>


                <div class="form-style-2">
                    <div class="form-style-2-heading">Provide your information</div>
                    <form action="" method="post">
                        <label for="field1"><span>Name <span class="required">*</span></span><input type="text" class="input-field" name="field1" value="" /></label>
                        <label for="field2"><span>Email <span class="required">*</span></span><input type="text" class="input-field" name="field2" value="" /></label>
                        <label><span>Telephone</span><input type="text" class="tel-number-field" name="tel_no_1" value="" maxlength="4" />-<input type="text" class="tel-number-field" name="tel_no_2" value="" maxlength="4" />-<input type="text" class="tel-number-field" name="tel_no_3" value="" maxlength="10" /></label>
                        <label for="field4"><span>Regarding</span><select name="field4" class="select-field">
                            <option value="General Question">General</option>
                            <option value="Advertise">Advertisement</option>
                            <option value="Partnership">Partnership</option>
                        </select></label>
                        <label for="field5"><span>Message <span class="required">*</span></span><textarea name="field5" class="textarea-field"></textarea></label>

                        <label><span> </span><input type="submit" value="Submit" /></label>
                    </form>
                </div>



            </div>
        );
    }
}

export default AddReview;
