import React, { Component } from 'react';
import './App.css';

class AllReviews extends Component {

    // Initialize state
    state = {}

    componentDidMount() {
        let url = "http://localhost:3004/reviews";
        fetch(url)
            .then(resp => resp.json())
            .then(data => {
                let posts = data.map((reviews, index) => {
                    return (
                        <div key={index}>
                            <h3>reviews.id</h3>
                        </div>

                    )
                })
                this.setState({ posts: posts });
            })

    }



    render() {

        return (
            <div>
                <p className="App">
                    {this.state.posts}
                </p>
            </div>
        );
    }
}

export default AllReviews;
